package desafio.gbx.desafio.controller;import desafio.gbx.desafio.dto.LoginDto;import desafio.gbx.desafio.entity.UsuarioEntity;import desafio.gbx.desafio.service.TokenService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.security.authentication.AuthenticationManager;import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/v1/login")public class AutenticacaoController {    @Autowired    private AuthenticationManager authenticationManager;    @Autowired    TokenService tokenService;    @PostMapping()    public ResponseEntity login(@RequestBody LoginDto loginDto){        var token = new UsernamePasswordAuthenticationToken(loginDto.getNumeroConta(), loginDto.getSenha());        var autenticacao = authenticationManager.authenticate(token);        return ResponseEntity.ok().body(tokenService.geraToken((UsuarioEntity) autenticacao.getPrincipal()));    }}