package desafio.gbx.desafio.controller;import desafio.gbx.desafio.entity.TransacaoEntity;import desafio.gbx.desafio.service.TransacaoService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.Optional;@RestController@RequestMapping("/v1/transacao")public class TransacaoController {    @Autowired    private TransacaoService transacaoService;    @PostMapping("/criar")    public ResponseEntity criarTransacao(@RequestBody TransacaoEntity transacao){        return transacaoService.criarTransacao(transacao);    }    @GetMapping("/consulta/{id}")    public ResponseEntity consultaTransacao(@PathVariable Integer id){        return transacaoService.consultaTransacao(id);    }}