package desafio.gbx.desafio.controller;import desafio.gbx.desafio.entity.UsuarioEntity;import desafio.gbx.desafio.service.UsuarioService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/v1/usuario")public class UsuarioController {    @Autowired    private UsuarioService usuarioService;    @PostMapping("/criar")    public ResponseEntity criarUsuario(@RequestBody UsuarioEntity usuario){        return usuarioService.criarUsuario(usuario);    }    @GetMapping("/listar")    public ResponseEntity<List<UsuarioEntity>> listarUsuario(){        return usuarioService.listarUsuario();    }}