package desafio.gbx.desafio.entity;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@Entity@NoArgsConstructor@AllArgsConstructorpublic class UsuarioEntity {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private long id;    private String nome;    private long numeroConta;    private double saldo;    public UsuarioEntity(String nome, long numeroConta, double saldo) {        this.nome = nome;        this.numeroConta = numeroConta;        this.saldo = saldo;    }}