package desafio.gbx.desafio.repository;import desafio.gbx.desafio.entity.UsuarioEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;@Repositorypublic interface UsuarioRepository extends JpaRepository<UsuarioEntity, Long> {    @Query(value = "select * from Usuario_Entity where numero_Conta = ?", nativeQuery = true)    UsuarioEntity existConta(long id);}