package desafio.gbx.desafio.repository;import desafio.gbx.desafio.entity.UsuarioEntity;import jakarta.persistence.EntityManager;import static org.assertj.core.api.Assertions.assertThat;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.autoconfigure.orm.jpa.DataJpaTest;import org.springframework.test.context.ActiveProfiles;import java.util.Objects;@DataJpaTest@ActiveProfiles("test")class UsuarioRepositoryTest {    @Autowired    EntityManager entityManager;    @Autowired    UsuarioRepository usuarioRepository;    @Test    @DisplayName("verifica se existe conta 1")    void existContaExiste() {        UsuarioEntity usuario = new UsuarioEntity("bruno", 50, 5000);        createUser(usuario);        var resposta = usuarioRepository.existConta(50);        assertThat(Objects.isNull(resposta)).isFalse();    }    @Test    @DisplayName("verifica se existe conta 2")    void existContaNaoExiste() {        UsuarioEntity usuario = new UsuarioEntity("bruno", 50, 5000);        var resposta = usuarioRepository.existConta(50);        assertThat(Objects.isNull(resposta)).isTrue();    }    private UsuarioEntity createUser(UsuarioEntity usuario){        this.entityManager.persist(usuario);        return usuario;    }}