package desafio.gbx.desafio.service;import desafio.gbx.desafio.entity.UsuarioEntity;import desafio.gbx.desafio.repository.UsuarioRepository;import org.junit.jupiter.api.Assertions;import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import org.mockito.InjectMocks;import org.mockito.Mock;import org.mockito.MockitoAnnotations;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import static org.mockito.Mockito.when;class UsuarioServiceTest {    @Mock    private UsuarioRepository usuarioRepository;    @Autowired    @InjectMocks    UsuarioService usuarioService;    @BeforeEach    void setup(){        MockitoAnnotations.initMocks(this);    }    @Test    void criarUsuario() {        UsuarioEntity usuario = new UsuarioEntity(1, "bruno", 1,5000);        when(usuarioRepository.save(usuario)).thenReturn(usuario);        Assertions.assertEquals(usuarioService.criarUsuario(usuario), ResponseEntity.status(201).body(usuario));    }}